tables.table_schema:
CREATE TABLE HealthcareProviders (
  provider_id UUID PRIMARY KEY,
  provider_name VARCHAR(100) NOT NULL,
  provider_address VARCHAR(255) NOT NULL
);

CREATE TABLE Patients (
  patient_id UUID PRIMARY KEY,
  patient_name VARCHAR(100) NOT NULL,
  patient_age INT NOT NULL,
  patient_gender VARCHAR(10) NOT NULL
);

CREATE TABLE Transactions (
  transaction_id UUID PRIMARY KEY,
  record_id UUID REFERENCES MedicalRecords(record_id),
  transaction_date TIMESTAMP NOT NULL,
  amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE healthcare_providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(255),
    license_number VARCHAR(50),
    specialization VARCHAR(100)
);

CREATE TABLE blockchain_data (
    block_id INT PRIMARY KEY,
    record_id INT UNIQUE,
    block_hash VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE doctors (
    doctor_id INT PRIMARY KEY,
    doctor_name VARCHAR(50),
    doctor_specialty VARCHAR(50),
    doctor_location VARCHAR(100)
);

CREATE TABLE blockchain_data (
    block_id INT PRIMARY KEY,
    record_id INT,
    block_hash VARCHAR(64),
    block_timestamp TIMESTAMP
);
