tables.table_schema:
CREATE TABLE participants (
    participant_id UUID PRIMARY KEY,
    participant_name VARCHAR(50),
    participant_age INT,
    participant_gender VARCHAR(10),
    participant_status VARCHAR(20)
);

CREATE TABLE studies (
    study_id UUID PRIMARY KEY,
    study_name VARCHAR(100),
    study_description TEXT,
    study_start_date DATE,
    study_end_date DATE
);

CREATE TABLE study_participant_mapping (
    map_id UUID PRIMARY KEY,
    study_id UUID,
    participant_id UUID,
    enrollment_date DATE
);

CREATE TABLE participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(50),
    participant_age INT,
    study_id INT
);

CREATE TABLE study_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(50),
    site_location VARCHAR(100),
    study_id INT
);

CREATE TABLE blockchain_network (
    network_id INT PRIMARY KEY,
    network_name VARCHAR(50),
    network_type VARCHAR(50),
    security_level VARCHAR(20)
);
