tables.table_schema:
CREATE TABLE asset_ownership (
    asset_id UUID PRIMARY KEY,
    owner_address VARCHAR(255) NOT NULL,
    transfer_date TIMESTAMP NOT NULL
);

CREATE TABLE asset_ownership (
    asset_id INT PRIMARY KEY,
    owner_address VARCHAR(255),
    transfer_date TIMESTAMP
);

CREATE TABLE asset_transfer (
    transfer_id INT PRIMARY KEY,
    asset_id INT,
    previous_owner VARCHAR(255),
    new_owner VARCHAR(255),
    transfer_date TIMESTAMP
);

CREATE TABLE recording_ownership (
  record_id SERIAL PRIMARY KEY,
  asset_name VARCHAR(255) NOT NULL,
  owner_name VARCHAR(255) NOT NULL,
  transfer_date DATE
);

CREATE TABLE token_ownership (
  token_id SERIAL PRIMARY KEY,
  contract_id INT REFERENCES tokenization_smart_contracts(contract_id),
  token_owner VARCHAR(255) NOT NULL,
  transfer_date DATE
);

CREATE TABLE recording_ownership (
    id INT PRIMARY KEY,
    asset_name VARCHAR(255),
    owner_address VARCHAR(255),
    contract_address VARCHAR(255)
);

CREATE TABLE contract_tokens (
    contract_id INT PRIMARY KEY,
    owner_address VARCHAR(255),
    token_id INT,
    token_address VARCHAR(255)
);

CREATE TABLE asset_owners (
  owner_id INT PRIMARY KEY,
  owner_name VARCHAR(255) NOT NULL,
  asset_id INT NOT NULL
);

CREATE TABLE assets (
  asset_id INT PRIMARY KEY,
  asset_name VARCHAR(255) NOT NULL,
  asset_type VARCHAR(50) NOT NULL,
  token_price DECIMAL(10,2)
);

CREATE TABLE token_owners (
  token_owner_id INT PRIMARY KEY,
  token_owner_name VARCHAR(255) NOT NULL
);

CREATE TABLE asset_tokens (
  asset_id INT NOT NULL,
  token_id INT NOT NULL,
  token_owner_id INT NOT NULL,
  token_balance INT NOT NULL
);
