tables.table_schema:
CREATE TABLE Properties (
    property_id UUID PRIMARY KEY,
    property_address VARCHAR(255) NOT NULL,
    owner_name VARCHAR(100) NOT NULL,
    owner_contact VARCHAR(20) NOT NULL
);

CREATE TABLE Owners (
    owner_id UUID PRIMARY KEY,
    owner_name VARCHAR(100) NOT NULL,
    contact_number VARCHAR(20) NOT NULL,
    email_address VARCHAR(255) NOT NULL
);

CREATE TABLE property_owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(50) NOT NULL,
    property_id INT NOT NULL,
    ownership_percentage DECIMAL(5,2) NOT NULL
);

CREATE TABLE properties (
    property_id INT PRIMARY KEY,
    property_address VARCHAR(100) NOT NULL,
    property_value DECIMAL(10,2) NOT NULL
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    property_id INT NOT NULL,
    transaction_date DATE NOT NULL,
    transaction_amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE audit_trail (
    audit_id INT PRIMARY KEY,
    transaction_id INT NOT NULL,
    audit_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE PropertyOwners ( 
    OwnerID INT PRIMARY KEY, 
    OwnerName VARCHAR(255) NOT NULL, 
    OwnerAddress VARCHAR(255) NOT NULL 
);

CREATE TABLE Properties ( 
    PropertyID INT PRIMARY KEY, 
    PropertyAddress VARCHAR(255) NOT NULL, 
    PropertyValue DECIMAL(10, 2) NOT NULL 
);

CREATE TABLE OwnershipHistory ( 
    TransactionID INT PRIMARY KEY, 
    PropertyID INT NOT NULL, 
    OwnerID INT NOT NULL, 
    TransactionDate DATE NOT NULL 
);

CREATE TABLE PropertyDocuments ( 
    DocumentID INT PRIMARY KEY, 
    PropertyID INT NOT NULL, 
    DocumentType VARCHAR(50) NOT NULL, 
    DocumentLink VARCHAR(255) NOT NULL 
);
