tables.table_schema:
CREATE TABLE Backers (
	backer_id INT PRIMARY KEY,
	backer_name VARCHAR(255),
	backer_email VARCHAR(255),
	total_contribution DECIMAL(10, 2)
);

CREATE TABLE Investors (
    investor_id UUID PRIMARY KEY,
    investor_name VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE,
    total_investment DECIMAL(12, 2)
);

CREATE TABLE Payments (
    payment_id UUID PRIMARY KEY,
    investment_id UUID REFERENCES Investments(investment_id),
    payment_amount DECIMAL(12, 2) NOT NULL,
    payment_date TIMESTAMP NOT NULL
);

CREATE TABLE projects (
    project_id INT PRIMARY KEY,
    project_name TEXT NOT NULL,
    project_description TEXT,
    start_date DATE,
    end_date DATE,
    funding_goal DECIMAL,
    funds_raised DECIMAL
);

CREATE TABLE backers (
    backer_id INT PRIMARY KEY,
    backer_name TEXT NOT NULL,
    backer_email TEXT NOT NULL,
    total_invested DECIMAL
);

CREATE TABLE investments (
    investment_id INT PRIMARY KEY,
    backer_id INT REFERENCES backers(backer_id),
    project_id INT REFERENCES projects(project_id),
    amount_invested DECIMAL,
    investment_date DATE
);

CREATE TABLE rewards (
    reward_id INT PRIMARY KEY,
    project_id INT REFERENCES projects(project_id),
    reward_description TEXT,
    reward_value DECIMAL
);

CREATE TABLE blockchain_transactions (
    transaction_id INT PRIMARY KEY,
    project_id INT REFERENCES projects(project_id),
    transaction_type TEXT
);
