tables.table_schema:
CREATE TABLE voting_records (
    voter_id INT PRIMARY KEY,
    candidate_name VARCHAR(255) NOT NULL,
    vote_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE election_results (
    candidate_name VARCHAR(255) PRIMARY KEY,
    total_votes INT NOT NULL,
    percentage_votes DECIMAL(5,2) NOT NULL
);

CREATE TABLE voting_data (
    voter_id INT PRIMARY KEY,
    candidate_name VARCHAR(255) NOT NULL,
    vote_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE candidate_status (
    candidate_id INT PRIMARY KEY,
    candidate_name VARCHAR(255) NOT NULL,
    votes_received INT DEFAULT 0
);

CREATE TABLE election_results (
    election_id INT PRIMARY KEY,
    winner_name VARCHAR(255) NOT NULL,
    total_votes INT NOT NULL
);

CREATE TABLE VotingRecords (
    voter_id INT PRIMARY KEY,
    candidate_id INT,
    vote_timestamp TIMESTAMP
);

CREATE TABLE Voters (
    voter_id INT PRIMARY KEY,
    voter_name VARCHAR(255),
    voter_address VARCHAR(255)
);

CREATE TABLE Candidates (
    candidate_id INT PRIMARY KEY,
    candidate_name VARCHAR(255),
    party_affiliation VARCHAR(255)
);

CREATE TABLE AuditTrail (
    audit_id INT PRIMARY KEY,
    audit_timestamp TIMESTAMP,
    event_description VARCHAR(255)
);

CREATE TABLE voters (
   voter_id INT PRIMARY KEY,
   voter_name VARCHAR(50) NOT NULL,
   voted_candidate VARCHAR(50) NOT NULL
);

CREATE TABLE candidates (
   candidate_id INT PRIMARY KEY,
   candidate_name VARCHAR(50) NOT NULL
);

CREATE TABLE election_results (
   result_id INT PRIMARY KEY,
   candidate_id INT,
   total_votes INT,
   FOREIGN KEY (candidate_id) REFERENCES candidates(candidate_id)
);

CREATE TABLE audit_log (
   log_id INT PRIMARY KEY,
   action VARCHAR(100) NOT NULL,
   timestamp TIMESTAMP NOT NULL
);
