tables.table_schema:
CREATE TABLE CandidateProfile (
    candidate_id INT PRIMARY KEY,
    candidate_name VARCHAR(255),
    age INT,
    party_affiliation VARCHAR(50)
);

CREATE TABLE EligibilityCriteria (
    criteria_id INT PRIMARY KEY,
    criteria_description VARCHAR(255)
);

CREATE TABLE CandidateEligibility (
    candidate_id INT,
    criteria_id INT,
    FOREIGN KEY (candidate_id) REFERENCES CandidateProfile(candidate_id),
    FOREIGN KEY (criteria_id) REFERENCES EligibilityCriteria(criteria_id)
);

CREATE TABLE CandidateRegions (
 id UUID PRIMARY KEY,
 region_name VARCHAR(255)
);

CREATE TABLE CandidateSkills (
 id UUID PRIMARY KEY,
 skill_name VARCHAR(255),
 proficiency_level VARCHAR(50)
);

CREATE TABLE CandidateVotes (
 id UUID PRIMARY KEY,
 candidate_id UUID,
 voter_id UUID
);
