tables.table_schema:
CREATE TABLE VoterEligibility (
    voter_id INT PRIMARY KEY,
    is_eligible BOOLEAN,
    eligibility_reason TEXT
);

CREATE TABLE eligibility_criteria(
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(50) NOT NULL,
    minimum_age INT,
    allowed_address_prefix VARCHAR(20)
);

CREATE TABLE vote_records(
    record_id INT PRIMARY KEY,
    voter_id INT,
    voted_at TIMESTAMP,
    verifying_officer VARCHAR(50)
);

CREATE TABLE voter_eligibility (
    voter_id INT PRIMARY KEY,
    is_eligible BOOLEAN NOT NULL,
    eligibility_reason TEXT
);

CREATE TABLE voter_history (
    voter_id INT PRIMARY KEY,
    last_voted_date DATE,
    voting_frequency INT
);

CREATE TABLE voter_issued_id (
    voter_id INT PRIMARY KEY,
    id_type VARCHAR(20) NOT NULL,
    id_number VARCHAR(50) NOT NULL
);

CREATE TABLE voter_verification (
    voter_id INT PRIMARY KEY,
    verification_status BOOLEAN NOT NULL,
    verification_date DATE NOT NULL
);
