tables.table_schema:
CREATE TABLE verification_process (
    voter_id INT PRIMARY KEY,
    voter_name VARCHAR(50) NOT NULL,
    verification_status BOOLEAN
);

CREATE TABLE verification_process (
    id SERIAL PRIMARY KEY,
    voter_name VARCHAR(255),
    verification_status BOOLEAN
);

CREATE TABLE voting_registration (
    id SERIAL PRIMARY KEY,
    voter_id INT,
    registration_status BOOLEAN,
    registration_date DATE
);

CREATE TABLE verification_logs (
    log_id INT PRIMARY KEY,
    request_id INT,
    log_date TIMESTAMP,
    log_description TEXT
);

CREATE TABLE verification_results (
    result_id INT PRIMARY KEY,
    request_id INT,
    verification_result VARCHAR(50),
    result_date TIMESTAMP
);

CREATE TABLE verification ( 
    verification_id INT PRIMARY KEY, 
    voter_id INT NOT NULL, 
    verification_code VARCHAR(10) NOT NULL 
);

CREATE TABLE voter ( 
    voter_id INT PRIMARY KEY, 
    voter_name VARCHAR(50) NOT NULL, 
    voter_email VARCHAR(50), 
    voter_phone VARCHAR(15) 
);

CREATE TABLE vote ( 
    vote_id INT PRIMARY KEY, 
    voter_id INT NOT NULL, 
    candidate_id INT NOT NULL, 
    vote_timestamp TIMESTAMP NOT NULL 
);

CREATE TABLE candidate ( 
    candidate_id INT PRIMARY KEY, 
    candidate_name VARCHAR(50) NOT NULL, 
    candidate_party VARCHAR(50) NOT NULL 
);

CREATE TABLE Voters (
    voter_id INT PRIMARY KEY,
    voter_name VARCHAR(50) NOT NULL,
    voter_email VARCHAR(100) NOT NULL
);

CREATE TABLE Candidates (
    candidate_id INT PRIMARY KEY,
    candidate_name VARCHAR(50) NOT NULL,
    candidate_party VARCHAR(50) NOT NULL
);

CREATE TABLE Votes (
    vote_id INT PRIMARY KEY,
    voter_id INT NOT NULL,
    candidate_id INT NOT NULL,
    vote_time TIMESTAMP NOT NULL
);

CREATE TABLE Verification_Log (
    log_id INT PRIMARY KEY,
    verification_status VARCHAR(20) NOT NULL,
    verification_time TIMESTAMP NOT NULL
);

CREATE TABLE Audit_Log (
    audit_id INT PRIMARY KEY,
    action_performed VARCHAR(100) NOT NULL,
    action_time TIMESTAMP NOT NULL,
    user_id INT NOT NULL
);
