tables.table_schema:
CREATE TABLE loyalty_points (
  customer_id INT,
  tokens INT
);

CREATE TABLE loyalty_points (
    customer_id INT,
    points INT
);

CREATE TABLE tokens (
    token_id INT,
    customer_id INT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50) NOT NULL,
    loyalty_points INT
);

CREATE TABLE tokens (
    token_id INT PRIMARY KEY,
    token_value VARCHAR(20) NOT NULL,
    customer_id INT,
    FOREIGN KEY (customer_id) REFERENCES customers(customer_id)
);

CREATE TABLE customers (
    customer_id UUID PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE loyalty_program (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(50) NOT NULL,
    expiration_date DATE NOT NULL,
    token_value INT NOT NULL
);

CREATE TABLE customer_tokens (
    token_id UUID PRIMARY KEY,
    customer_id UUID REFERENCES customers(customer_id),
    program_id INT REFERENCES loyalty_program(program_id),
    token_amount INT NOT NULL
);

CREATE TABLE transactions (
    transaction_id UUID PRIMARY KEY,
    token_id UUID REFERENCES customer_tokens(token_id),
    transaction_date TIMESTAMP NOT NULL,
    transaction_type VARCHAR(50) NOT NULL
);

CREATE TABLE customer_table (
    customer_id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(50),
    loyalty_points INT
);

CREATE TABLE token_table (
    token_id INT PRIMARY KEY,
    token_value VARCHAR(50),
    expiration_date DATE
);

CREATE TABLE reward_table (
    reward_id INT PRIMARY KEY,
    reward_name VARCHAR(50),
    reward_cost INT
);

CREATE TABLE redemption_table (
    redemption_id INT PRIMARY KEY,
    customer_id INT,
    reward_id INT,
    redemption_date DATE
);
