tables.table_schema:
CREATE TABLE asset_tokenization (
    asset_token_id UUID PRIMARY KEY,
    token_owner VARCHAR(100),
    token_creation_date TIMESTAMP,
    token_value DECIMAL(10, 2)
);

CREATE TABLE SupplyChainTransactions (
    transaction_id UUID PRIMARY KEY,
    transaction_timestamp TIMESTAMP NOT NULL,
    product_name VARCHAR(255) NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE AssetTokenization (
    token_id UUID PRIMARY KEY,
    asset_id UUID NOT NULL,
    token_owner VARCHAR(255) NOT NULL,
    token_symbol VARCHAR(10) NOT NULL
);

CREATE TABLE Authentication (
    authentication_id UUID PRIMARY KEY,
    token_id UUID NOT NULL,
    authentication_timestamp TIMESTAMP NOT NULL,
    authenticated_by VARCHAR(255) NOT NULL
);

CREATE TABLE product_details (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR(100) NOT NULL,
    description TEXT,
    manufacturer_id UUID NOT NULL
);

CREATE TABLE manufacturer_details (
    manufacturer_id UUID PRIMARY KEY,
    manufacturer_name VARCHAR(100) NOT NULL,
    contact_email VARCHAR(100) NOT NULL,
    headquarters_location VARCHAR(100) NOT NULL
);

CREATE TABLE transactions (
   transaction_id SERIAL PRIMARY KEY,
   transaction_date TIMESTAMP,
   transaction_type TEXT,
   asset_id INT
);

CREATE TABLE assets (
   asset_id SERIAL PRIMARY KEY,
   asset_name TEXT,
   asset_value FLOAT,
   asset_owner_id INT
);

CREATE TABLE owners (
   owner_id SERIAL PRIMARY KEY,
   owner_name TEXT,
   owner_contact TEXT
);

CREATE TABLE transactions_history (
   history_id SERIAL PRIMARY KEY,
   transaction_id INT,
   token_id INT,
   history_date TIMESTAMP
);
