tables.table_schema:
CREATE TABLE asset_ownership (
    asset_id VARCHAR(50) PRIMARY KEY,
    token_id VARCHAR(50),
    owner_name VARCHAR(100),
    owner_address VARCHAR(200)
);

CREATE TABLE assets (
    asset_id INT PRIMARY KEY,
    asset_name TEXT,
    token_id INT,
    owner_id INT
);

CREATE TABLE tokenization_records (
    token_id TEXT PRIMARY KEY,
    asset_id UUID,
    tokenization_date TIMESTAMP
);

CREATE TABLE user_accounts (
    user_id UUID PRIMARY KEY,
    username TEXT,
    email TEXT,
    registration_date TIMESTAMP
);

CREATE TABLE assets (
    asset_id UUID PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    owner_id UUID NOT NULL,
    token_id UUID NOT NULL
);

CREATE TABLE owners (
    owner_id UUID PRIMARY KEY,
    owner_name VARCHAR(255) NOT NULL,
    contact_info VARCHAR(255)
);

CREATE TABLE tokens (
    token_id UUID PRIMARY KEY,
    token_symbol VARCHAR(10) NOT NULL,
    token_type VARCHAR(20),
    token_value DECIMAL(18, 2) NOT NULL
);

CREATE TABLE asset_tokens (
    token_id UUID PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    token_value DECIMAL(18, 2) NOT NULL,
    token_owner VARCHAR(255) NOT NULL
);

CREATE TABLE ownership (
    ownership_id UUID PRIMARY KEY,
    token_id UUID REFERENCES asset_tokens(token_id),
    owner_name VARCHAR(255) NOT NULL,
    owner_address VARCHAR(255) NOT NULL
);

CREATE TABLE transactions (
    transaction_id UUID PRIMARY KEY,
    token_id UUID REFERENCES asset_tokens(token_id),
    transaction_type VARCHAR(50) NOT NULL,
    transaction_date TIMESTAMP NOT NULL
);

CREATE TABLE audit_trail (
    audit_id UUID PRIMARY KEY,
    transaction_id UUID REFERENCES transactions(transaction_id),
    description TEXT NOT NULL,
    audit_date TIMESTAMP NOT NULL
);

CREATE TABLE user_roles (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    role VARCHAR(50) NOT NULL
);
