tables.table_schema:
CREATE TABLE assets (
  asset_id INT PRIMARY KEY,
  asset_name VARCHAR(255) NOT NULL,
  asset_type VARCHAR(50) NOT NULL,
  asset_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE owners (
  owner_id INT PRIMARY KEY,
  owner_name VARCHAR(255) NOT NULL,
  owner_email VARCHAR(255) UNIQUE NOT NULL,
  owner_phone VARCHAR(20)
);

CREATE TABLE authentication (
  auth_id INT PRIMARY KEY,
  asset_id INT NOT NULL,
  auth_token VARCHAR(255) NOT NULL,
  auth_expiry TIMESTAMP
);

CREATE TABLE AssetTokens (
    asset_id INT PRIMARY KEY,
    token_id INT UNIQUE,
    asset_name VARCHAR(255),
    token_owner VARCHAR(255)
);

CREATE TABLE AssetOwners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(255),
    total_assets_owned INT
);

CREATE TABLE Authenticators (
    authenticator_id INT PRIMARY KEY,
    authenticator_name VARCHAR(255),
    authentication_key VARCHAR(50),
    trusted BOOLEAN
);
