CREATE TABLE tokenized_assets (
    asset_id INT PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    asset_type VARCHAR(50) NOT NULL,
    token_type VARCHAR(50) NOT NULL
);

CREATE TABLE shareholder_votes (
    vote_id SERIAL PRIMARY KEY,
    asset_id INT NOT NULL,
    shareholder_id INT NOT NULL,
    vote_choice BOOLEAN NOT NULL,
    vote_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE tokenized_assets (
  asset_id UUID PRIMARY KEY,
  asset_name TEXT,
  token_price DECIMAL(10, 2)
);

CREATE TABLE shareholders (
  shareholder_id UUID PRIMARY KEY,
  shareholder_name TEXT,
  token_balance INT
);

CREATE TABLE tokenized_assets (
    asset_id UUID PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    total_tokens INTEGER NOT NULL
);

CREATE TABLE shareholders (
    shareholder_id UUID PRIMARY KEY,
    shareholder_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL
);

CREATE TABLE shareholder_votes (
    vote_id UUID PRIMARY KEY,
    voting_id UUID NOT NULL,
    vote_choice BOOLEAN NOT NULL,
    FOREIGN KEY (voting_id) REFERENCES voting_rights(voting_id)
);

CREATE TABLE tokenized_assets (
    asset_id UUID PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    total_tokens INTEGER NOT NULL,
    token_price DECIMAL(10,2) NOT NULL
);

CREATE TABLE shareholders (
    shareholder_id UUID PRIMARY KEY,
    shareholder_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL UNIQUE
);

CREATE TABLE voting_rights (
    shareholder_id UUID REFERENCES shareholders(shareholder_id),
    asset_id UUID REFERENCES tokenized_assets(asset_id),
    voting_power INTEGER NOT NULL DEFAULT 1
);

CREATE TABLE votes (
    vote_id UUID PRIMARY KEY,
    shareholder_id UUID REFERENCES shareholders(shareholder_id),
    asset_id UUID REFERENCES tokenized_assets(asset_id),
    vote_decision BOOLEAN NOT NULL
);
