tables.table_schema:
CREATE TABLE assets (
  asset_id INT PRIMARY KEY,
  asset_name TEXT,
  token_amount INT
);

CREATE TABLE tokens (
    token_id UUID PRIMARY KEY,
    token_name VARCHAR(100) NOT NULL,
    token_value DECIMAL NOT NULL
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    user_name VARCHAR(100) NOT NULL,
    user_email VARCHAR(100) UNIQUE
);

CREATE TABLE TokenizedAssets (
  asset_id INT PRIMARY KEY,
  asset_name VARCHAR(50),
  asset_type VARCHAR(50),
  asset_value DECIMAL(10, 2)
);

CREATE TABLE Lenders (
  lender_id INT PRIMARY KEY,
  lender_name VARCHAR(50),
  lender_contact VARCHAR(100),
  lender_balance DECIMAL(10, 2)
);

