tables.table_schema:
CREATE TABLE temperature_readings (
    temperature_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    location VARCHAR(255),
    temperature FLOAT
);

CREATE TABLE goods (
    goods_id INT PRIMARY KEY,
    name VARCHAR(255),
    perishable BOOLEAN,
    storage_temperature FLOAT
);

CREATE TABLE perishable_goods (
    id INT PRIMARY KEY,
    product_name TEXT,
    temperature FLOAT,
    condition TEXT
);

CREATE TABLE temperature_logs (
    log_id INT PRIMARY KEY,
    product_id INT,
    temperature FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE product_condition (
    condition_id INT PRIMARY KEY,
    product_id INT,
    condition_description TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE temperature_logs (
  temperature_id INT PRIMARY KEY,
  temperature float,
  timestamp TIMESTAMP
);

CREATE TABLE location_logs (
  location_id INT PRIMARY KEY,
  latitude float,
  longitude float,
  timestamp TIMESTAMP
);

CREATE TABLE perishable_goods (
  goods_id INT PRIMARY KEY,
  goods_name string,
  temperature_threshold float,
  condition string
);

CREATE TABLE alerts (
  alert_id INT PRIMARY KEY,
  alert_message string,
  timestamp TIMESTAMP
);
