tables.table_schema:
CREATE TABLE smart_meter_data (
    id INT PRIMARY KEY,
    meter_id INT NOT NULL,
    reading_timestamp TIMESTAMP NOT NULL,
    reading_value FLOAT NOT NULL
);

CREATE TABLE energy_distribution (
    distribution_id INT PRIMARY KEY,
    distribution_point VARCHAR(255) NOT NULL,
    voltage_level INT NOT NULL,
    current_load FLOAT NOT NULL
);

CREATE TABLE smart_meters (
    meter_id SERIAL PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    manufacturer VARCHAR(255) NOT NULL,
    installation_date DATE
);

CREATE TABLE electricity_readings (
    reading_id SERIAL PRIMARY KEY,
    meter_id INT REFERENCES smart_meters(meter_id),
    reading_date TIMESTAMP,
    reading_value FLOAT
);

CREATE TABLE energy_consumption (
    consumption_id SERIAL PRIMARY KEY,
    meter_id INT REFERENCES smart_meters(meter_id),
    date DATE,
    consumed_energy FLOAT
);

CREATE TABLE anomalies (
    anomaly_id SERIAL PRIMARY KEY,
    meter_id INT REFERENCES smart_meters(meter_id),
    anomaly_date TIMESTAMP,
    description TEXT
);
