tables.table_schema:
CREATE TABLE SolarPanelEnergyProduction ( 
  panel_id INT PRIMARY KEY, 
  production_date DATE, 
  production_time TIME, 
  energy_produced DECIMAL(10,2) 
);

CREATE TABLE SolarPanelCertification (
    panel_id INT PRIMARY KEY,
    production_date DATE,
    production_time TIME,
    production_amount DECIMAL(10, 2)
);

CREATE TABLE CertificationAgency (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(50),
    contact_person VARCHAR(50),
    certification_status ENUM('pending', 'approved', 'rejected')
);

CREATE TABLE SolarPanels (
    panel_id INT PRIMARY KEY,
    panel_brand VARCHAR(255),
    panel_capacity DOUBLE PRECISION,
    installation_date DATE
);

CREATE TABLE EnergyProduction (
    production_id INT PRIMARY KEY,
    panel_id INT REFERENCES SolarPanels(panel_id),
    production_date DATE,
    production_amount DOUBLE PRECISION
);

CREATE TABLE Certification (
    certification_id INT PRIMARY KEY,
    production_id INT REFERENCES EnergyProduction(production_id),
    certification_date DATE,
    certification_result BOOLEAN
);

CREATE TABLE solar_panels (
  panel_id INT PRIMARY KEY,
  panel_location VARCHAR(255),
  panel_area FLOAT,
  panel_angle FLOAT
);

CREATE TABLE inspectors (
  inspector_id INT PRIMARY KEY,
  inspector_name VARCHAR(100),
  inspector_email VARCHAR(255),
  inspector_phone VARCHAR(20)
);

CREATE TABLE PanelInformation (
    panel_id INT PRIMARY KEY,
    panel_location VARCHAR(100),
    panel_capacity DECIMAL
);

CREATE TABLE CertificationAgency (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(100),
    contact_person VARCHAR(100)
);

CREATE TABLE EnergyUsage (
    id INT PRIMARY KEY,
    usage_date DATE,
    usage_time TIME,
    energy_consumed DECIMAL
);
