tables.table_schema:
CREATE TABLE carbon_emissions (
   id INT PRIMARY KEY,
   emission_type VARCHAR(255),
   emission_amount FLOAT,
   emission_timestamp TIMESTAMP
);

CREATE TABLE carbon_offsets (
   id INT PRIMARY KEY,
   offset_type VARCHAR(255),
   offset_amount FLOAT,
   offset_timestamp TIMESTAMP
);

CREATE TABLE CarbonEmissions (
    id INT PRIMARY KEY,
    emission_source VARCHAR(255),
    emission_date DATE,
    emission_amount DECIMAL(10, 2)
);

CREATE TABLE BlockchainTransactions (
    tx_id UUID PRIMARY KEY,
    tx_timestamp TIMESTAMP,
    tx_type VARCHAR(255),
    related_offset_id INT REFERENCES EmissionOffsets(id)
);

CREATE TABLE SustainabilityReports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    report_content TEXT,
    related_tx_id UUID REFERENCES BlockchainTransactions(tx_id)
);

CREATE TABLE carbon_offsets (
    id INT PRIMARY KEY,
    emission_id INT,
    offset_amount DECIMAL,
    offset_date DATE
);

CREATE TABLE carbon_emissions (
    id INT PRIMARY KEY,
    company_id INT,
    emission_value DECIMAL,
    emission_date DATE
);

CREATE TABLE blockchain_transactions (
    id INT PRIMARY KEY,
    transaction_hash VARCHAR(255),
    block_number INT
);

CREATE TABLE companies (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    address VARCHAR(255),
    industry VARCHAR(255)
);

CREATE TABLE blockchain_info (
    id INT PRIMARY KEY,
    block_hash VARCHAR(255),
    miner_id INT,
    transaction_id INT
);
