tables.table_schema:
CREATE TABLE energy_producers (
    producer_id UUID PRIMARY KEY,
    producer_name VARCHAR,
    location VARCHAR,
    energy_type VARCHAR
);

CREATE TABLE energy_consumers (
    consumer_id UUID PRIMARY KEY,
    consumer_name VARCHAR,
    location VARCHAR,
    energy_usage DECIMAL
);

CREATE TABLE energy_transactions(
    transaction_id INT NOT NULL,
    energy_amount DECIMAL NOT NULL,
    transaction_date DATE NOT NULL,
    transaction_type VARCHAR(50) NOT NULL
);

CREATE TABLE energy_providers(
    provider_id INT NOT NULL,
    provider_name VARCHAR(100) NOT NULL,
    provider_location VARCHAR(100) NOT NULL
);

CREATE TABLE energy_consumers(
    consumer_id INT NOT NULL,
    consumer_name VARCHAR(100) NOT NULL,
    consumer_location VARCHAR(100) NOT NULL
);

CREATE TABLE transaction_history(
    history_id INT NOT NULL,
    transaction_id INT NOT NULL,
    event_type VARCHAR(50) NOT NULL,
    event_date DATETIME NOT NULL
);

CREATE TABLE EnergyConsumers (
    consumer_id INT PRIMARY KEY,
    consumer_name VARCHAR(100),
    address VARCHAR(255),
    energy_usage FLOAT
);

CREATE TABLE EnergyProviders (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(100),
    address VARCHAR(255)
);
