tables.table_schema:
CREATE TABLE Energy_Consumers (
    consumer_id UUID PRIMARY KEY,
    consumer_name VARCHAR(255),
    address VARCHAR(255),
    email VARCHAR(255)
);

CREATE TABLE SmartMeters (
    meter_id UUID PRIMARY KEY,
    meter_type VARCHAR(50) NOT NULL,
    installation_date TIMESTAMP NOT NULL,
    customer_id UUID NOT NULL
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    user_type VARCHAR(50) NOT NULL,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(255) NOT NULL
);

CREATE TABLE EnergyConsumers (
    consumer_id UUID PRIMARY KEY,
    consumer_name VARCHAR(255),
    address VARCHAR(255),
    energy_usage DECIMAL
);

CREATE TABLE EnergyProviders (
    provider_id UUID PRIMARY KEY,
    provider_name VARCHAR(255),
    energy_type VARCHAR(255)
);

CREATE TABLE SmartContracts (
    contract_id UUID PRIMARY KEY,
    contract_name VARCHAR(255),
    contract_details TEXT,
    status VARCHAR(50)
);
