tables.table_schema:
CREATE TABLE energy_consumption (
    timestamp TIMESTAMP PRIMARY KEY,
    device_id INT,
    energy_consumed DECIMAL
);

CREATE TABLE blockchain_records (
    block_id SERIAL PRIMARY KEY,
    block_hash VARCHAR(256),
    prev_block_hash VARCHAR(256),
    timestamp TIMESTAMP
);

CREATE TABLE energy_producers (
    producer_id UUID PRIMARY KEY,
    producer_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    production_capacity INT NOT NULL
);

CREATE TABLE energy_consumers (
    consumer_id UUID PRIMARY KEY,
    consumer_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    energy_consumed DECIMAL(10,2) NOT NULL
);

CREATE TABLE blockchain_metadata (
    block_id UUID PRIMARY KEY,
    previous_block_id UUID,
    merkle_root VARCHAR(255) NOT NULL,
    timestamp TIMESTAMP
);

CREATE TABLE EnergyConsumers (
    consumer_id INT PRIMARY KEY,
    consumer_name VARCHAR(50) NOT NULL,
    energy_consumed DECIMAL NOT NULL,
    timestamp TIMESTAMP
);

CREATE TABLE EnergyProducers (
    producer_id INT PRIMARY KEY,
    producer_name VARCHAR(50) NOT NULL,
    energy_produced DECIMAL NOT NULL,
    timestamp TIMESTAMP
);

CREATE TABLE EnergySupplyCompanies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(50) NOT NULL,
    energy_revenue DECIMAL NOT NULL,
    timestamp TIMESTAMP
);
