tables.table_schema:
CREATE TABLE product_recalls (
  id INT PRIMARY KEY,
  product_name VARCHAR,
  recall_date DATE,
  reason VARCHAR
);

CREATE TABLE emergency_information (
  id INT PRIMARY KEY,
  emergency_type VARCHAR,
  location VARCHAR,
  details VARCHAR
);

CREATE TABLE product_recall_history (
  id SERIAL PRIMARY KEY,
  product_name VARCHAR(255) NOT NULL,
  reason TEXT,
  recall_date DATE
);

CREATE TABLE recall_impact (
  impact_id SERIAL PRIMARY KEY,
  recall_id INT REFERENCES product_recall_history(id),
  affected_region VARCHAR(100) NOT NULL,
  impact_description TEXT
);

CREATE TABLE emergency_contacts (
  contact_id SERIAL PRIMARY KEY,
  contact_name VARCHAR(100) NOT NULL,
  contact_number VARCHAR(20) NOT NULL,
  email VARCHAR(255)
);

