tables.table_schema:
CREATE TABLE user_identities (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    encrypted_password VARCHAR(100),
    public_key VARCHAR(150)
);

CREATE TABLE blockchain_authentication (
    id INT PRIMARY KEY,
    user_id INT REFERENCES user_identities(id),
    authentication_token VARCHAR(200)
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    password VARCHAR(100) NOT NULL
);

CREATE TABLE user_roles (
    role_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    role_name VARCHAR(50) NOT NULL,
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE blockchain_auth (
    auth_id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    auth_key VARCHAR(100) NOT NULL,
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password_hash BYTEA NOT NULL,
    public_key BYTEA NOT NULL
);

CREATE TABLE user_roles (
    role_id SERIAL PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL
);

CREATE TABLE user_tokens (
    token_id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES users(user_id) NOT NULL,
    token_value BYTEA NOT NULL
);

CREATE TABLE user_sessions (
    session_id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES users(user_id) NOT NULL,
    session_key BYTEA NOT NULL
);

CREATE TABLE blockchain_authentication (
    authentication_id SERIAL PRIMARY KEY,
    user_id INTEGER REFERENCES users(user_id) NOT NULL,
    blockchain_address VARCHAR(100) NOT NULL
);
