tables.table_schema:
CREATE TABLE blockchain_monitoring (
    transaction_id INT PRIMARY KEY,
    transaction_hash VARCHAR(255),
    transaction_type VARCHAR(50),
    suspicious_activity BOOLEAN
);

CREATE TABLE suspicious_activity_logs (
  log_id SERIAL PRIMARY KEY,
  transaction_id UUID NOT NULL,
  suspicious_reason TEXT NOT NULL,
  log_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE blockchain_users (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  registration_date DATE NOT NULL
);

CREATE TABLE security_analysis_reports (
  report_id SERIAL PRIMARY KEY,
  report_title TEXT NOT NULL,
  analysis_date TIMESTAMP NOT NULL,
  analyst_id UUID NOT NULL
);

CREATE TABLE blockchain_addresses (
    id SERIAL PRIMARY KEY,
    address VARCHAR(255) NOT NULL,
    balance DECIMAL NOT NULL
);
