CREATE TABLE blockchain_network (
    node_id UUID PRIMARY KEY,
    node_address VARCHAR,
    node_role VARCHAR
);

CREATE TABLE blocks (
    block_id INT PRIMARY KEY,
    previous_block_id INT,
    hash VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE blockchain (
    block_id INT PRIMARY KEY,
    transaction_id INT,
    hash VARCHAR(255)
);

CREATE TABLE nodes (
    node_id INT PRIMARY KEY,
    node_address VARCHAR(255),
    is_validator BOOLEAN
);

CREATE TABLE blockchain_network ( 
    block_number BIGINT PRIMARY KEY, 
    previous_block_hash VARCHAR(255) NOT NULL, 
    block_hash VARCHAR(255) UNIQUE NOT NULL 
);

CREATE TABLE transaction_status ( 
    transaction_id UUID PRIMARY KEY, 
    block_number BIGINT NOT NULL, 
    status VARCHAR(20) NOT NULL, 
    processed_timestamp TIMESTAMP 
);

CREATE TABLE nodes ( 
    node_id UUID PRIMARY KEY, 
    node_address VARCHAR(255) UNIQUE NOT NULL, 
    is_validator BOOLEAN NOT NULL 
);

CREATE TABLE node_relations ( 
    parent_node_id UUID NOT NULL, 
    child_node_id UUID NOT NULL, 
    PRIMARY KEY (parent_node_id, child_node_id) 
);
