tables.table_schema:
CREATE TABLE blockchain_transactions (
    transaction_id INT PRIMARY KEY,
    user_id INT,
    amount DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE anonymized_transactions (
    transaction_id INT PRIMARY KEY,
    anonymized_user_id INT,
    amount DECIMAL,
    timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id INT,
    username VARCHAR(50),
    email VARCHAR(100),
    address VARCHAR(255)
);

CREATE TABLE transactions (
    transaction_id INT,
    user_id INT,
    transaction_date TIMESTAMP,
    transaction_amount DECIMAL(10, 2)
);

CREATE TABLE anon_transactions (
    anon_transaction_id INT,
    user_id INT,
    transaction_date TIMESTAMP,
    transaction_amount DECIMAL(10, 2)
);

CREATE TABLE user_access_logs (
    log_id SERIAL PRIMARY KEY,
    user_id INT,
    access_timestamp TIMESTAMP
);
