CREATE TABLE labeled_training_data (
    id INT PRIMARY KEY,
    data TEXT NOT NULL,
    label TEXT NOT NULL,
    created_at TIMESTAMP
);

CREATE TABLE labeled_training_data (
    id INT PRIMARY KEY,
    data TEXT,
    label VARCHAR(50),
    created_at TIMESTAMP
);

CREATE TABLE machine_learning_models (
    model_id SERIAL PRIMARY KEY,
    model_name VARCHAR(100),
    model_type VARCHAR(50),
    training_data_id INT,
    FOREIGN KEY (training_data_id) REFERENCES labeled_training_data(id)
);

CREATE TABLE labeled_training_data (
    id SERIAL PRIMARY KEY,
    data JSONB NOT NULL,
    label TEXT NOT NULL
);

CREATE TABLE machine_learning_models (
    model_id UUID PRIMARY KEY,
    model_name TEXT NOT NULL,
    hyperparameters JSONB
);

CREATE TABLE training_logs (
    log_id SERIAL PRIMARY KEY,
    model_id UUID NOT NULL,
    accuracy FLOAT NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE labeled_data (
    data_id INT PRIMARY KEY,
    data_value VARCHAR(255),
    label VARCHAR(50)
);

CREATE TABLE data_labels (
    label_id INT PRIMARY KEY,
    label_name VARCHAR(50)
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(100)
);
