tables.table_schema:
CREATE TABLE ml_features (
  id INT PRIMARY KEY,
  feature_name VARCHAR(50),
  data_type VARCHAR(20),
  description TEXT
);

CREATE TABLE feature_extraction (
   id INT PRIMARY KEY,
   feature_name VARCHAR(50),
   feature_value DOUBLE PRECISION
);

CREATE TABLE data_preprocessing (
   id INT PRIMARY KEY,
   dataset_id INT,
   normalization_method VARCHAR(50)
);

CREATE TABLE model_training (
   id INT PRIMARY KEY,
   model_name VARCHAR(50),
   algorithm_type VARCHAR(50),
   accuracy_score DOUBLE PRECISION
);

CREATE TABLE model_evaluation (
   id INT PRIMARY KEY,
   model_id INT,
   evaluation_metric VARCHAR(50),
   evaluation_score DOUBLE PRECISION
);

CREATE TABLE features (
    feature_id INT PRIMARY KEY,
    feature_name VARCHAR(255) NOT NULL,
    feature_type VARCHAR(50) NOT NULL,
    feature_value FLOAT NOT NULL
);

CREATE TABLE data_samples (
    sample_id INT PRIMARY KEY,
    feature_id INT NOT NULL,
    sample_data TEXT NOT NULL
);

CREATE TABLE labels (
    label_id INT PRIMARY KEY,
    label_name VARCHAR(50) NOT NULL
);

CREATE TABLE training_data (
    training_id INT PRIMARY KEY,
    sample_id INT NOT NULL,
    label_id INT NOT NULL
);

CREATE TABLE model_parameters (
    parameter_id INT PRIMARY KEY,
    parameter_name VARCHAR(50) NOT NULL,
    parameter_value FLOAT NOT NULL
);
