tables.table_schema:
CREATE TABLE fraudulent_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_description VARCHAR(255),
    is_active BOOLEAN
);

CREATE TABLE Customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    address VARCHAR(100),
    email VARCHAR(50) UNIQUE
);

CREATE TABLE FraudulentTransactions (
    fraud_id INT PRIMARY KEY,
    transaction_id INT,
    reason VARCHAR(100),
    FOREIGN KEY (transaction_id) REFERENCES Transactions(transaction_id)
);

CREATE TABLE SuspiciousCustomers (
    customer_id INT PRIMARY KEY,
    last_activity_date DATETIME,
    reason VARCHAR(100)
);

CREATE TABLE transactions (
   transaction_id INT PRIMARY KEY,
   customer_id INT,
   amount DECIMAL(10, 2),
   transaction_date TIMESTAMP
);

CREATE TABLE customers (
   customer_id INT PRIMARY KEY,
   name VARCHAR(50),
   email VARCHAR(100)
);

CREATE TABLE account_balance (
   customer_id INT,
   balance DECIMAL(10, 2),
   last_updated TIMESTAMP
);

CREATE TABLE fraud_patterns (
   pattern_id INT PRIMARY KEY,
   pattern_description VARCHAR(100)
);

CREATE TABLE flagged_transactions (
   transaction_id INT,
   flag_reason VARCHAR(100),
   flagger_id INT
);
