tables.table_schema:
CREATE TABLE summary_statistics (
  id INT PRIMARY KEY,
  dataset_name VARCHAR(255),
  numerical_attribute1 FLOAT,
  numerical_attribute2 FLOAT
);

CREATE TABLE dataset_table (
    id INT PRIMARY KEY,
    numerical_attribute1 FLOAT,
    numerical_attribute2 FLOAT
);

CREATE TABLE dataset_table (
   id INT PRIMARY KEY,
   attribute1 FLOAT,
   attribute2 FLOAT,
   attribute3 FLOAT
);

CREATE TABLE calculation_table (
   id INT PRIMARY KEY,
   calculation_type VARCHAR(50),
   result FLOAT
);
