tables.table_schema:
CREATE TABLE customer_reviews (
 review_id INT PRIMARY KEY,
 review_text TEXT
);

CREATE TABLE sentiment_analysis (
 analysis_id INT PRIMARY KEY,
 review_id INT REFERENCES customer_reviews(review_id),
 sentiment_score FLOAT
);

CREATE TABLE reviews (
    review_id INT PRIMARY KEY,
    customer_id INT,
    review_text TEXT,
    review_date DATE
);

CREATE TABLE customer_sentiments (
    customer_id INT PRIMARY KEY,
    total_reviews INT,
    average_sentiment FLOAT
);

CREATE TABLE review_statistics (
    statistics_id INT PRIMARY KEY,
    review_id INT,
    word_count INT,
    character_count INT
);

CREATE TABLE Customer_Reviews (
    review_id SERIAL PRIMARY KEY,
    review_content TEXT,
    customer_id INT,
    review_date DATE
);

CREATE TABLE Sentiment_Analysis (
    analysis_id SERIAL PRIMARY KEY,
    review_id INT,
    sentiment_score FLOAT,
    sentiment_category VARCHAR(50)
);

CREATE TABLE Customer_Details (
    customer_id SERIAL PRIMARY KEY,
    customer_name VARCHAR(100),
    email VARCHAR(100),
    phone_number VARCHAR(20)
);

CREATE TABLE Review_Tags (
    tag_id SERIAL PRIMARY KEY,
    review_id INT,
    tag_name VARCHAR(50)
);

CREATE TABLE Review_Analytics (
    analytics_id SERIAL PRIMARY KEY,
    review_id INT,
    word_count INT,
    char_count INT
);
