tables.table_schema:
CREATE TABLE lift_and_support_metrics (
    transaction_id INT,
    item_id INT,
    lift FLOAT,
    support FLOAT
);

CREATE TABLE transaction_data (
	transaction_id INT NOT NULL,
	item_id INT NOT NULL
);

CREATE TABLE association_rules (
	rule_id INT NOT NULL,
	lift_metric FLOAT NOT NULL,
	support_metric FLOAT NOT NULL
);

CREATE TABLE Lift_Support_Metrics (
    transaction_id INT,
    item_id INT,
    lift_metric FLOAT,
    support_metric FLOAT
);

CREATE TABLE Transactions (
    transaction_id INT,
    transaction_date DATE,
    total_items INT
);

CREATE TABLE Items (
    item_id INT,
    item_name VARCHAR(50),
    item_category VARCHAR(50)
);

CREATE TABLE lift_support_metrics (
    item_id INTEGER,
    lift_metric FLOAT,
    support_metric FLOAT,
    PRIMARY KEY (item_id)
);
