tables.table_schema:
CREATE TABLE sensor_data_table (
    sensor_id INT PRIMARY KEY,
    sensor_name VARCHAR(50),
    sensor_value FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE sensor_location_table (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE sensor_status_table (
    status_id INT PRIMARY KEY,
    sensor_id INT,
    status VARCHAR(20),
    last_updated TIMESTAMP
);

CREATE TABLE sensor_data (
  id INT PRIMARY KEY,
  sensor_id INT,
  timestamp TIMESTAMP,
  reading FLOAT
);

CREATE TABLE sensors (
  sensor_id INT PRIMARY KEY,
  sensor_name VARCHAR(50),
  location VARCHAR(100)
);

CREATE TABLE alerts (
  alert_id INT PRIMARY KEY,
  sensor_id INT,
  timestamp TIMESTAMP,
  message TEXT
);

CREATE TABLE readings (
  reading_id INT PRIMARY KEY,
  sensor_id INT,
  value FLOAT,
  timestamp TIMESTAMP
);
