tables.table_schema:
CREATE TABLE DataLake (
    data_id INT PRIMARY KEY,
    file_name VARCHAR(255),
    file_type VARCHAR(50),
    data_content TEXT
);

CREATE TABLE DataTags (
    tag_id INT PRIMARY KEY,
    data_id INT,
    tag_name VARCHAR(50),
    FOREIGN KEY (data_id) REFERENCES DataLake(data_id)
);

CREATE TABLE DataLake (
    id INT PRIMARY KEY,
    file_name TEXT,
    file_size INT,
    upload_time TIMESTAMP
);

CREATE TABLE Tags (
    tag_id INT PRIMARY KEY,
    tag_name TEXT
);

CREATE TABLE data_sources (
    id INT PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    source_type VARCHAR(50) NOT NULL
);

CREATE TABLE data_records (
    record_id INT PRIMARY KEY,
    source_id INT NOT NULL,
    record_data JSONB NOT NULL,
    record_date TIMESTAMP NOT NULL
);

CREATE TABLE data_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(100) NOT NULL
);

CREATE TABLE data_connections (
    connection_id INT PRIMARY KEY,
    source_id INT NOT NULL,
    destination_id INT NOT NULL
);

CREATE TABLE data_lake_metadata (
    metadata_id INT PRIMARY KEY,
    file_id INT REFERENCES data_lake_files(file_id),
    metadata_key VARCHAR(50),
    metadata_value TEXT
);

CREATE TABLE data_lake_tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50) UNIQUE
);

CREATE TABLE data_lake_file_tags (
    file_tag_id INT PRIMARY KEY,
    file_id INT REFERENCES data_lake_files(file_id),
    tag_id INT REFERENCES data_lake_tags(tag_id)
);

CREATE TABLE data_lake_logs (
    log_id INT PRIMARY KEY,
    action VARCHAR(50),
    timestamp TIMESTAMP,
    user_id INT
);
