tables.table_schema:
CREATE TABLE anomalies (
    id INT PRIMARY KEY,
    anomaly_description VARCHAR(255),
    anomaly_type VARCHAR(50),
    detection_date DATE
);

CREATE TABLE queries (
    id INT PRIMARY KEY,
    query_text TEXT,
    query_date DATE,
    user_id INT
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    join_date DATE
);

CREATE TABLE anomaly_detection (
    id INT PRIMARY KEY,
    query_name VARCHAR(255) NOT NULL,
    query_description TEXT,
    anomaly_threshold FLOAT
);

CREATE TABLE query_execution (
    execution_id SERIAL PRIMARY KEY,
    query_id INT REFERENCES anomaly_detection(id),
    execution_time TIMESTAMPTZ NOT NULL
);

CREATE TABLE anomaly_results (
    result_id SERIAL PRIMARY KEY,
    execution_id INT REFERENCES query_execution(execution_id),
    result_description TEXT,
    anomaly_score FLOAT
);

CREATE TABLE query_logs (
    log_id SERIAL PRIMARY KEY,
    execution_id INT REFERENCES query_execution(execution_id),
    log_message TEXT,
    log_time TIMESTAMPTZ NOT NULL
);
