tables.table_schema:
CREATE TABLE extraction_and_transformation (
    data_source VARCHAR(255),
    data_type VARCHAR(50),
    extraction_method VARCHAR(100),
    transformation_query TEXT
);

CREATE TABLE extraction_sources (
    id SERIAL PRIMARY KEY,
    source_name VARCHAR,
    source_type VARCHAR,
    source_url VARCHAR
);

CREATE TABLE extraction_sources ( 
    source_id INT PRIMARY KEY, 
    source_name VARCHAR(255) 
);

CREATE TABLE data_transformation ( 
    transformation_id INT PRIMARY KEY, 
    transformation_name VARCHAR(255), 
    transformation_type VARCHAR(50) 
);

CREATE TABLE ExtractData (
    id INT PRIMARY KEY,
    source VARCHAR(100),
    data_type VARCHAR(50),
    last_extract_date DATE
);

CREATE TABLE TransformData (
    id INT PRIMARY KEY,
    transform_type VARCHAR(50),
    transformation_script TEXT
);

CREATE TABLE SourceMapping (
    id INT PRIMARY KEY,
    source_name VARCHAR(100),
    source_type VARCHAR(50),
    target_table VARCHAR(100)
);

CREATE TABLE ReportDefinition (
    id INT PRIMARY KEY,
    report_name VARCHAR(100),
    report_description TEXT,
    created_date DATE
);

CREATE TABLE ReportData (
    id INT PRIMARY KEY,
    report_id INT,
    report_data JSON,
    report_date DATE
);
