tables.table_schema:
CREATE TABLE knowledge_graph (
    entity_id INT PRIMARY KEY,
    entity_name VARCHAR(255),
    relation_type VARCHAR(50),
    related_entity_id INT
);

CREATE TABLE KnowledgeGraphNodes (
    node_id UUID PRIMARY KEY,
    node_label TEXT
);

CREATE TABLE KnowledgeGraphEdges (
    edge_id UUID PRIMARY KEY,
    source_node_id UUID,
    target_node_id UUID,
    edge_label TEXT
);

CREATE TABLE entity_nodes (
  entity_id UUID PRIMARY KEY,
  entity_name STRING
);

CREATE TABLE relationships (
  relationship_id UUID PRIMARY KEY,
  relationship_name STRING
);

CREATE TABLE entity_relationships (
  entity_id UUID,
  relationship_id UUID,
  FOREIGN KEY (entity_id) REFERENCES entity_nodes(entity_id),
  FOREIGN KEY (relationship_id) REFERENCES relationships(relationship_id)
);

CREATE TABLE entities (entity_id INT PRIMARY KEY,
                entity_name VARCHAR(255) NOT NULL,
                entity_type VARCHAR(50) NOT NULL);

CREATE TABLE entity_properties (property_id INT PRIMARY KEY,
                        property_name VARCHAR(50) NOT NULL,
                        property_value TEXT);
