tables.table_schema:
CREATE TABLE social_media_data (
    post_id INT PRIMARY KEY,
    post_content TEXT,
    platform_name VARCHAR(50),
    sentiment_score FLOAT
);

CREATE TABLE trend_prediction (
    prediction_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    confidence_score FLOAT
);

CREATE TABLE sentiment_analysis ( 
	id INT PRIMARY KEY, 
	user_id INT, 
	post TEXT, 
	sentiment_score FLOAT 
);

CREATE TABLE user_profile ( 
	user_id INT PRIMARY KEY, 
	username VARCHAR(50), 
	followers_count INT, 
	platform VARCHAR(20) 
);

CREATE TABLE trend_prediction ( 
	id INT PRIMARY KEY, 
	trend_name VARCHAR(50), 
	hashtag VARCHAR(20), 
	prediction_score FLOAT 
);

CREATE TABLE social_media_platform ( 
	platform_id INT PRIMARY KEY, 
	platform_name VARCHAR(50), 
	company VARCHAR(50), 
	founded_date DATE 
);

CREATE TABLE social_media_posts (
    post_id INT PRIMARY KEY,
    post_content TEXT,
    sentiment_score FLOAT,
    post_date TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    account_created_at TIMESTAMP
);

CREATE TABLE hashtags (
    hashtag_id INT PRIMARY KEY,
    hashtag_name VARCHAR(50) UNIQUE
);

CREATE TABLE trend_predictions (
    prediction_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    prediction_date TIMESTAMP,
    prediction_score FLOAT
);
