tables.table_schema:
CREATE TABLE neural_network_weights (
    layer_name VARCHAR(255),
    weight_matrix FLOAT[],
    bias_vector FLOAT[],
    PRIMARY KEY (layer_name)
);

CREATE TABLE neural_network_layers (
    layer_id SERIAL PRIMARY KEY,
    layer_name VARCHAR(255) NOT NULL,
    weights JSONB NOT NULL,
    biases JSONB NOT NULL
);

CREATE TABLE network_training_data (
    data_id SERIAL PRIMARY KEY,
    layer_id INT NOT NULL,
    input_data JSONB NOT NULL,
    output_data JSONB NOT NULL,
    FOREIGN KEY (layer_id) REFERENCES neural_network_layers(layer_id)
);

CREATE TABLE neural_network_layers (
    layer_id INT PRIMARY KEY,
    layer_name VARCHAR(50),
    weights BLOB,
    biases BLOB
);

CREATE TABLE layer_weights (
    layer_id INT PRIMARY KEY,
    weights BLOB
);

CREATE TABLE layer_biases (
    layer_id INT PRIMARY KEY,
    biases BLOB
);

CREATE TABLE neuron_activations (
    activation_id INT PRIMARY KEY,
    activation_value FLOAT
);

CREATE TABLE fine_tuning_settings (
    setting_id INT PRIMARY KEY,
    learning_rate FLOAT,
    regularization_factor FLOAT
);

CREATE TABLE layer_weights (
	layer_id INT PRIMARY KEY,
	weight_data BYTEA
);

CREATE TABLE layer_biases (
	layer_id INT PRIMARY KEY,
	bias_data BYTEA
);

CREATE TABLE neural_network (
	network_id INT PRIMARY KEY,
	network_name VARCHAR(255)
);

CREATE TABLE fine_tuning_info (
	fine_tuning_id INT PRIMARY KEY,
	layer_id INT,
	learning_rate FLOAT
);

CREATE TABLE model_metrics (
	metric_id INT PRIMARY KEY,
	network_id INT,
	accuracy FLOAT,
	loss FLOAT
);
