tables.table_schema:
CREATE TABLE MarketOpportunities (
    id INT PRIMARY KEY,
    opportunity_name VARCHAR(255),
    description TEXT,
    industry_sector VARCHAR(100)
);

CREATE TABLE BusinessDecisions (
    decision_id INT PRIMARY KEY,
    decision_title VARCHAR(255),
    decision_description TEXT,
    decision_date DATE
);

CREATE TABLE market_opportunities (
  id SERIAL PRIMARY KEY,
  opportunity_name VARCHAR(255) NOT NULL,
  description TEXT,
  market_size INTEGER
);

CREATE TABLE business_decisions (
  id SERIAL PRIMARY KEY,
  decision_name VARCHAR(255) NOT NULL,
  decision_category VARCHAR(100),
  decision_date DATE
);

CREATE TABLE sql_data (
  id SERIAL PRIMARY KEY,
  data_name VARCHAR(255) NOT NULL,
  data_value DECIMAL,
  data_type VARCHAR(50)
);

CREATE TABLE external_market_data (
  id SERIAL PRIMARY KEY,
  source_name VARCHAR(255) NOT NULL,
  data_description TEXT,
  data_date DATE
);

CREATE TABLE companies (
    company_id UUID PRIMARY KEY,
    company_name TEXT NOT NULL,
    industry TEXT,
    revenue DECIMAL
);

CREATE TABLE products (
    product_id UUID PRIMARY KEY,
    product_name TEXT NOT NULL,
    category TEXT,
    price DECIMAL
);

CREATE TABLE market_data (
    data_id UUID PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    stock_price DECIMAL,
    market_volume INT
);

CREATE TABLE opportunities (
    opportunity_id UUID PRIMARY KEY,
    opportunity_desc TEXT NOT NULL,
    opportunity_type TEXT,
    expected_return DECIMAL
);

CREATE TABLE decisions (
    decision_id UUID PRIMARY KEY,
    decision_desc TEXT NOT NULL,
    decision_type TEXT,
    impact_score INT
);
