tables.table_schema:
CREATE TABLE DataCleansing (
    id INT PRIMARY KEY,
    data varchar(255),
    cleaning_status varchar(50)
);

CREATE TABLE DataTransformation (
    id INT PRIMARY KEY,
    original_data varchar(255),
    transformed_data varchar(255)
);

CREATE TABLE DataCleansing (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    age INT,
    address VARCHAR(100)
);

CREATE TABLE DataQualityImprovement (
    id INT PRIMARY KEY,
    metric_name VARCHAR(50),
    value DECIMAL,
    threshold DECIMAL
);

CREATE TABLE DataCleansing (
  id UUID PRIMARY KEY,
  data_source TEXT NOT NULL,
  cleansing_method TEXT,
  cleansing_status TEXT
);

CREATE TABLE TransformationProcess (
  id UUID PRIMARY KEY,
  process_name TEXT NOT NULL,
  transformation_script TEXT NOT NULL,
  transformation_status TEXT
);

CREATE TABLE AnalyticsTasks (
  id UUID PRIMARY KEY,
  task_name TEXT NOT NULL,
  task_description TEXT,
  task_status TEXT
);

CREATE TABLE data_quality_jobs (
    job_id INT PRIMARY KEY,
    job_name VARCHAR(255),
    job_description TEXT,
    job_status VARCHAR(50)
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255),
    source_type VARCHAR(50),
    connection_string TEXT
);

CREATE TABLE data_cleansing_rules (
    rule_id INT PRIMARY KEY,
    rule_name VARCHAR(255),
    rule_description TEXT,
    rule_query TEXT
);

CREATE TABLE data_transformations (
    transformation_id INT PRIMARY KEY,
    transformation_name VARCHAR(255),
    transformation_description TEXT,
    transformation_query TEXT
);

CREATE TABLE data_quality_reports (
    report_id INT PRIMARY KEY,
    report_name VARCHAR(255),
    report_description TEXT,
    report_status VARCHAR(50)
);
