tables.table_schema:
CREATE TABLE aggregated_data (
  id SERIAL PRIMARY KEY,
  data_source VARCHAR(50),
  data_category VARCHAR(50),
  data_value DECIMAL
);

CREATE TABLE analytics_results (
  result_id SERIAL PRIMARY KEY,
  analysis_category VARCHAR(50),
  analysis_value DECIMAL
);

CREATE TABLE source_data_table (
  id INT PRIMARY KEY,
  data_source VARCHAR(255),
  data_category VARCHAR(255),
  data_value INT
);

CREATE TABLE processed_data_table (
  id INT PRIMARY KEY,
  data_source VARCHAR(255),
  data_category VARCHAR(255),
  processed_value DECIMAL
);

CREATE TABLE analytics_table (
  id INT PRIMARY KEY,
  data_source VARCHAR(255),
  data_category VARCHAR(255),
  aggregated_value DECIMAL
);

CREATE TABLE data_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(50) NOT NULL,
    source_type VARCHAR(50) NOT NULL,
    data_format VARCHAR(50) NOT NULL
);

