tables.table_schema:
CREATE TABLE outliers_data (
    id INT PRIMARY KEY,
    value FLOAT
);

CREATE TABLE outliers_analysis (
    id INT PRIMARY KEY,
    data_id INT,
    std_deviation FLOAT,
    FOREIGN KEY (data_id) REFERENCES outliers_data(id)
);

CREATE TABLE outliers_results (
    id INT PRIMARY KEY,
    analysis_id INT,
    outlier_value FLOAT,
    FOREIGN KEY (analysis_id) REFERENCES outliers_analysis(id)
);

CREATE TABLE outliers_data (
    id INT NOT NULL,
    value FLOAT NOT NULL,
    PRIMARY KEY (id)
);

CREATE TABLE standard_deviation (
