tables.table_schema:
CREATE TABLE categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50)
);

CREATE TABLE category_summary (
  summary_id INT PRIMARY KEY,
  category_id INT,
  summary_value FLOAT,
  FOREIGN KEY (category_id) REFERENCES categories(category_id)
);

CREATE TABLE distribution_summary (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(50) NOT NULL,
    total_count INT NOT NULL
);

CREATE TABLE category_counts (
    count_id INT PRIMARY KEY,
    category_id INT REFERENCES distribution_summary(category_id),
    count_value INT NOT NULL
);

CREATE TABLE category_percentage (
    percentage_id INT PRIMARY KEY,
    category_id INT REFERENCES distribution_summary(category_id),
    percentage DECIMAL(5,2) NOT NULL
);

CREATE TABLE CategoryDistribution (
  category_id INT,
  category_name VARCHAR(255),
  count INT
);

CREATE TABLE CategorySummary (
  summary_id INT,
  category_id INT,
  total_count INT
);

CREATE TABLE CategoryPercentage (
  percentage_id INT,
  category_id INT,
  percent DECIMAL(5,2)
);

CREATE TABLE distribution_summary (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    count INT
);

CREATE TABLE category_counts (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    total_count INT
);

CREATE TABLE category_avg (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    avg_count FLOAT
);

CREATE TABLE category_max (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    max_count INT
);

CREATE TABLE category_min (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255),
    min_count INT
);
