tables.table_schema:
CREATE TABLE mutations (
    id INT PRIMARY KEY,
    gene_name TEXT,
    mutation_type TEXT,
    mutation_position INT
);

CREATE TABLE genomic_data (
  sample_id INT PRIMARY KEY,
  patient_id INT,
  mutation_name VARCHAR(50),
  mutation_type VARCHAR(50)
);

CREATE TABLE sequencing_results (
  sample_id INT PRIMARY KEY,
  sequencing_date DATE,
  sequence_data TEXT,
  quality_score INT
);

CREATE TABLE mutation_statistics (
  mutation_id INT PRIMARY KEY,
  mutation_name VARCHAR(50),
  mutation_count INT,
  last_updated TIMESTAMP
);

CREATE TABLE researcher_information (
  researcher_id INT PRIMARY KEY,
  researcher_name VARCHAR(50),
  research_area VARCHAR(50),
  institution VARCHAR(100)
);
