tables.table_schema:
CREATE TABLE patients_genetic_data (
    patient_id INT PRIMARY KEY,
    patient_name VARCHAR(255),
    genetic_sequence TEXT,
    diagnosis VARCHAR(255)
);

CREATE TABLE PatientGeneticData (
    patient_id INT,
    genetic_sequence TEXT,
    age INT,
    gender TEXT
);

CREATE TABLE MedicalHistory (
    patient_id INT,
    diagnosis TEXT,
    treatment_plan TEXT
);

CREATE TABLE GeneticVariants (
    variant_id INT,
    gene_name TEXT,
    type TEXT,
    impact TEXT
);

CREATE TABLE ClinicalTrials (
    trial_id INT,
    trial_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE ResearchResults (
    result_id INT,
    conclusion TEXT,
    publication_date DATE
);
