CREATE TABLE drugs (
  drug_id INT PRIMARY KEY,
  drug_name VARCHAR(255),
  target_gene VARCHAR(255),
  mechanism_of_action TEXT
);

CREATE TABLE gene_expression (
  sample_id INT PRIMARY KEY,
  gene_name VARCHAR(255),
  expression_level FLOAT,
  sample_type VARCHAR(50)
);

CREATE TABLE gene_expression_data (
  gene_id UUID PRIMARY KEY,
  gene_name STRING,
  expression_level FLOAT
);

CREATE TABLE drug_targets (
  target_id UUID PRIMARY KEY,
  target_name STRING,
  target_function STRING
);

CREATE TABLE gene_drug_mapping (
  mapping_id UUID PRIMARY KEY,
  gene_id UUID REFERENCES gene_expression_data(gene_id),
  target_id UUID REFERENCES drug_targets(target_id)
);

CREATE TABLE gene_expression_data (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    expression_level FLOAT
);

CREATE TABLE drug_targets (
    target_id INT PRIMARY KEY,
    target_gene_id INT,
    target_type VARCHAR(50)
);

CREATE TABLE drug_data (
    drug_id INT PRIMARY KEY,
    drug_name VARCHAR(100),
    target_id INT,
    manufacturer VARCHAR(100)
);

CREATE TABLE gene_drug_relationship (
    gene_id INT,
    drug_id INT,
    CONSTRAINT fk_gene_id FOREIGN KEY (gene_id) REFERENCES gene_expression_data(gene_id),
    CONSTRAINT fk_drug_id FOREIGN KEY (drug_id) REFERENCES drug_data(drug_id)
);

CREATE TABLE gene_expression_data (
    gene_id INT PRIMARY KEY,
    gene_name VARCHAR(255),
    expression_level FLOAT
);

CREATE TABLE drug_targets (
    target_id INT PRIMARY KEY,
    target_name VARCHAR(255),
    gene_id INT REFERENCES gene_expression_data(gene_id),
    binding_affinity FLOAT
);

CREATE TABLE drugs (
    drug_id INT PRIMARY KEY,
    drug_name VARCHAR(255),
    target_id INT REFERENCES drug_targets(target_id),
    potency FLOAT
);

CREATE TABLE drug_categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255)
);

CREATE TABLE drug_category_mapping (
    mapping_id INT PRIMARY KEY,
    drug_id INT REFERENCES drugs(drug_id),
    category_id INT REFERENCES drug_categories(category_id)
);
