tables.table_schema:
CREATE TABLE mutations (
  mutation_id UUID PRIMARY KEY,
  gene_name VARCHAR(50),
  mutation_type VARCHAR(50),
  population_id UUID
);

CREATE TABLE populations (
  population_id UUID PRIMARY KEY,
  population_name VARCHAR(50),
  region VARCHAR(50)
);

CREATE TABLE samples (
  sample_id UUID PRIMARY KEY,
  sample_date DATE,
  population_id UUID,
  mutation_id UUID
);

CREATE TABLE diseases (
  disease_id UUID PRIMARY KEY,
  disease_name VARCHAR(50),
  mutation_id UUID
);

CREATE TABLE IF NOT EXISTS Population (
    id UUID PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE IF NOT EXISTS Mutation (
    id UUID PRIMARY KEY,
    mutation_type VARCHAR(50) NOT NULL,
    mutation_description TEXT
);

CREATE TABLE IF NOT EXISTS Individual (
    id UUID PRIMARY KEY,
    population_id UUID REFERENCES Population(id),
    mutation_id UUID REFERENCES Mutation(id),
    age INT
);

CREATE TABLE IF NOT EXISTS Investigation (
    id UUID PRIMARY KEY,
    individual_id UUID REFERENCES Individual(id),
    investigator VARCHAR(50) NOT NULL,
    date DATE
);

CREATE TABLE IF NOT EXISTS Disease (
    id UUID PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    description TEXT
);
