tables.table_schema:
CREATE TABLE genetic_markers (
    marker_id INT PRIMARY KEY,
    marker_name VARCHAR(100),
    trait_association VARCHAR(100),
    chromosome_number VARCHAR(10)
);

CREATE TABLE trait_information (
    trait_id INT PRIMARY KEY,
    trait_name VARCHAR(100),
    trait_description TEXT,
    marker_id INT
);

CREATE TABLE genotype (
    id INT PRIMARY KEY,
    gene_name VARCHAR(50),
    allele_1 VARCHAR(10),
    allele_2 VARCHAR(10)
);

CREATE TABLE trait (
    id INT PRIMARY KEY,
    trait_name VARCHAR(50),
    description TEXT
);

CREATE TABLE marker (
    id INT PRIMARY KEY,
    marker_name VARCHAR(50),
    location VARCHAR(50),
    trait_id INT REFERENCES trait(id)
);

CREATE TABLE markers (
  marker_id INT PRIMARY KEY,
  marker_name VARCHAR(50),
  chromosome VARCHAR(10),
  position INT
);

CREATE TABLE traits (
  trait_id INT PRIMARY KEY,
  trait_name VARCHAR(50),
  trait_description TEXT
);

CREATE TABLE marker_traits (
  marker_trait_id INT PRIMARY KEY,
  marker_id INT REFERENCES markers(marker_id),
  trait_id INT REFERENCES traits(trait_id),
  trait_value FLOAT
);

CREATE TABLE markers (
  marker_id INT PRIMARY KEY,
  marker_name VARCHAR(255),
  chromosome VARCHAR(10),
  position INT
);

CREATE TABLE traits (
  trait_id INT PRIMARY KEY,
  trait_name VARCHAR(255),
  trait_description TEXT
);

CREATE TABLE samples (
  sample_id INT PRIMARY KEY,
  sample_name VARCHAR(255),
  collection_date DATE
);
